pragma solidity ^0.4.22;

contract onlyStudents{
  constructor(){

  }

  address student;
  address teacher;

  function setStudent() private {
    student = msg.sender;
  }

  function setTeacher() private {
    teacher = msg.sender;
  }

  modifier onlyStudent{
    setStudent();
    require(student == msg.sender, "Incorrect Address");
    _;
  }

  modifier onlyTeacher{
    setTeacher();
    require(teacher==msg.sender, "Incorrect Address");
    _;
  }
}

contract EssayHub is onlyStudents {
  constructor() {
  }

  struct EssayInfo{
    string authorName;
    string essayName;
    string essayType;
    string hashfile;
    string ipfshash;
    uint32 time;
    uint32 Score;
    uint32 ShareCount;
    uint ExclusiveCost;
  }

  struct checkEssay{
    bool checkExistFile;
    bool checkExclusiveFile;
    uint id;
  }

  EssayInfo[] private essays;
  mapping (string => checkEssay) private verifyEssay;

  // The upload file system
  function uploadEssay(string authorName, string essayName, string essayType, string hashfile, string ipfshash, uint ExclusiveCost) public onlyStudent onlyTeacher returns(bool) {
    if (verifyEssay[hashfile].checkExistFile == true && verifyEssay[hashfile].checkExclusiveFile == false) {
      uint id = essays.push(EssayInfo(authorName, essayName, essayType, hashfile, ipfshash, uint32(now), 0, 0, ExclusiveCost));
      verifyEssay[hashfile].checkExistFile = true;
      verifyEssay[hashfile].checkExclusiveFile = false;
      verifyEssay[hashfile].id = id;
      return true;
    } 
    return false;
  }

  function checkEssayUploading(string hashfile) public returns (bool) {
    if (verifyEssay[hashfile].checkExistFile) {
      return false;
    } else if(verifyEssay[hashfile].checkExclusiveFile==true) {
      return false;
    }
    return true;
  }

  //Set id essay
  function getEssay(string hashfile) public returns (string Author, string Essay, string Type, uint32 Time, uint32 Score, uint32 ShareCount, uint ExclusiveCost) {
    if(verifyEssay[hashfile].checkExistFile == true && verifyEssay[hashfile].checkExclusiveFile == false){
      uint id = verifyEssay[hashfile].id;
      return (essays[id].authorName, essays[id].essayName, essays[id].essayType, essays[id].time, essays[id].Score, essays[id].ShareCount, essays[id].ExclusiveCost);
    }
    return;
  }

  // Voting system
  function votingByTeacher(string hashfile) public onlyTeacher{
    require(verifyEssay[hashfile].checkExistFile == true && verifyEssay[hashfile].checkExclusiveFile == false, "Not Access");
    uint id = verifyEssay[hashfile].id;
    essays[id].Score += 5;
  }
  function votingByOther(string hashfile) public {
    require(verifyEssay[hashfile].checkExistFile == true && verifyEssay[hashfile].checkExclusiveFile == false, "Not Access");
    uint id = verifyEssay[hashfile].id;
    essays[id].Score += 1;
  }

  // Classify view
  function getCostByShareView(string typeView, string hashfile) public returns (uint cost) {
    require(verifyEssay[hashfile].checkExistFile == true && verifyEssay[hashfile].checkExclusiveFile == false, "Not Access");
    if(keccak256(abi.encodePacked(typeView)) == keccak256(abi.encodePacked("Share"))){
      return 1 ether;
    }
    else{
      uint id = verifyEssay[hashfile].id;
      return essays[id].ExclusiveCost;
    }
  }

}
